document.addEventListener("DOMContentLoaded",function(){[".article-card .post-title a",".article-card .post-meta .post-meta-category .article-category-list-link",".article-card .post-footer .article-tag-list-link",".post-content .post-more",".post-list .post-title a"].forEach(function(t){document.querySelectorAll(t).forEach(function(t){t.addEventListener("mouseleave",function(){t.classList.add("hover-out"),setTimeout(function(){t.classList.remove("hover-out")},500)})})})});